mapper.select:
SELECT
FROM novel no WHERE no.id = #{id} AND no.delflag = #{delflag}
AND no.creator = IFNULL(#{creator}, no.creator)

SELECT
FROM novel no LEFT JOIN label la ON FIND_IN_SET(la.id, no.labels) LEFT JOIN classify cl ON no.classify = cl.id LEFT JOIN admin ad ON no.creator = ad.id LEFT JOIN (SELECT id as se_id, title as se_title, createTime as se_createTime, novelId FROM section WHERE novelId = #{id} ORDER BY createTime desc LIMIT 1) se ON se.novelId = no.id WHERE no.id = #{id} AND no.delflag = #{delflag} AND la.delflag = #{delflag}
AND no.creator = IFNULL(#{creator}, no.creator)

    SELECT 
    
    FROM novel no
    WHERE 

    
    AND no.`delflag` = #{delflag,jdbcType=TINYINT}
    
    
    AND no.`creator` = #{creator}
    
    
    AND no.`title` = #{title}
    
    
    AND no.`classify` = #{classify}
    
    
    AND no.`novelState` = #{novelState}

    SELECT 
    
    FROM novel no
    LEFT JOIN classify cl ON no.classify = cl.id
    LEFT JOIN label la ON no.id = la.targetId AND la.targetType = 4
    LEFT JOIN admin ad ON no.creator = ad.id
    LEFT JOIN section se ON no.lastetSectionId = se.id
    WHERE 

    
    AND no.`creator` = #{creator}
    
    
    AND LOCATE(#{title}, no.`title`) > 0
    
    
    AND no.`classify` = #{classify}
    
    
    AND LOCATE(#{label}, no.`labels`) > 0
    
    
    AND  UNIX_TIMESTAMP(no.createTime) >= #{startTime}  
    
    
    AND  UNIX_TIMESTAMP(no.createTime) <= #{endTime}  
    
    
    AND no.`delflag` = #{delflag,jdbcType=TINYINT}
    
    
    AND no.`novelState` = #{novelState}
    
    
    AND LOCATE(la.`name`, #{labels}) > 0
    

    GROUP BY no.id
    ORDER BY no.createTime desc
