mapper.select:
SELECT 

FROM resource re
WHERE re.id = #{id} AND re.delflag = #{delflag}

SELECT 

FROM resource re


AND re.`id` = #{id}


AND re.`pid` = #{pid}


AND re.`name` = #{name}


AND re.`request_url` = #{request_url}


AND re.`style` = #{style}


AND re.`type` = #{type}


AND re.`describes` = #{describes}


AND re.`key` = #{key}


AND re.`locking` = #{locking}


AND re.`state` = #{state,jdbcType=TINYINT}


AND re.`delflag` = #{delflag,jdbcType=TINYINT}

SELECT 

FROM
    admin_role ar 
LEFT JOIN
    role ro ON ar.rid = ro.id
INNER JOIN
    authority au ON au.roid = ro.id
INNER JOIN
    resource re ON re.id = au.reid
WHERE
    ar.aid = #{aid}
AND
    ar.delflag=#{delflag}
AND
    ro.delflag = #{delflag}
AND
    ro.state = #{state}
AND
    au.delflag=#{delflag}
AND
    au.state = #{state}
AND
    au.delflag = #{delflag}
AND
    re.delflag = #{delflag}
AND
    re.state = #{state}

SELECT re.* FROM admin ad INNER JOIN admin_role ar ON ad.id=ar.aid INNER JOIN role ro ON ar.rid = ro.id INNER JOIN authority au ON au.roid=ro.id INNER JOIN resource re ON re.id=au.reId WHERE 1=1
AND ad.id = #{adminId}
AND ad.accountName = #{adminCode}
AND re.type = #{type,jdbcType=TINYINT} AND ro.delflag = #{delflag,jdbcType=TINYINT} AND ar.delflag = #{delflag,jdbcType=TINYINT} AND au.delflag = #{delflag,jdbcType=TINYINT} AND re.delflag = #{delflag,jdbcType=TINYINT} AND ro.state=#{state,jdbcType=TINYINT} AND re.state=#{state,jdbcType=TINYINT}
