mapper.select:
SELECT 

FROM role ro
WHERE ro.id = #{id} 

AND ro.delflag = #{delflag}

mapper.update:
UPDATE
  admin_role
SET
  delflag = #{delflag, jdbcType=TINYINT}
AND
  modifier = #{modifier}
AND
  modifierTime = NOW()
WHERE
  aid = #{adminId}
AND
  rid IN

  #{roleId}

mapper.select:
SELECT

FROM role ro

  
    ro.creator = #{creator, jdbcType=INTEGER}
  
  
    AND ro.state = #{state,jdbcType=TINYINT}
  
  
    AND ro.delflag = #{delflag, jdbcType=TINYINT}
  

ORDER BY sort

SELECT , cr.nikename AS creatorName, mo.nikename AS modifierName FROM role ro LEFT JOIN admin cr ON ro.creator = cr.id LEFT JOIN admin mo ON ro.modifier = mo.id
ro.creator = #{creator, jdbcType=INTEGER}
AND ro.state = #{state,jdbcType=TINYINT}
AND ro.delflag = #{delflag, jdbcType=TINYINT}
AND cr.nikeName LIKE CONCAT('%', #{creatorName}, '%')

SELECT
  DISTINCT 
FROM role ro
LEFT JOIN admin_role ar ON ro.id = ar.rid
WHERE
  ro.delflag = #{delflag, jdbcType=TINYINT}
AND
  ro.state = #{state, jdbcType = TINYINT}


AND  
  ar.aid = #{adminId}


AND
  ar.aid != #{adminId}
